```csharp:RateLimitedStream.cs
using System;
using System.IO;
using System.Threading;
using System.Threading.Tasks;

public class RateLimitedStream : Stream
{
    private readonly Stream _baseStream;
    private readonly int _bytesPerSecond;
    private readonly Func<long> _getCurrentMilliseconds;
    private long _byteCount;
    private long _start;

    public RateLimitedStream(Stream baseStream, int bytesPerSecond, Func<long> getCurrentMilliseconds = null)
    {
        _baseStream = baseStream;
        _bytesPerSecond = bytesPerSecond;
        _getCurrentMilliseconds = getCurrentMilliseconds ?? (() => DateTimeOffset.UtcNow.ToUnixTimeMilliseconds());
        _start = _getCurrentMilliseconds();
    }

    public override bool CanRead => _baseStream.CanRead;
    public override bool CanSeek => _baseStream.CanSeek;
    public override bool CanWrite => _baseStream.CanWrite;
    public override long Length => _baseStream.Length;
    public override long Position
    {
        get => _baseStream.Position;
        set => _baseStream.Position = value;
    }

    public override void Flush() => _baseStream.Flush();
    public override long Seek(long offset, SeekOrigin origin) => _baseStream.Seek(offset, origin);
    public override void SetLength(long value) => _baseStream.SetLength(value);

    public override int Read(byte[] buffer, int offset, int count)
    {
        ApplyRateLimit(count);
        return _baseStream.Read(buffer, offset, count);
    }

    public override void Write(byte[] buffer, int offset, int count)
    {
        ApplyRateLimit(count);
        _baseStream.Write(buffer, offset, count);
    }

    private void ApplyRateLimit(int byteCount)
    {
        _byteCount += byteCount;
        long elapsedMilliseconds = _getCurrentMilliseconds() - _start;
        long expectedMilliseconds = (_byteCount * 1000) / _bytesPerSecond;

        if (expectedMilliseconds > elapsedMilliseconds)
        {
            Thread.Sleep((int)(expectedMilliseconds - elapsedMilliseconds));
        }
    }
}
```

