using System.Net;

class Program
{
    static void Main(string[] args)
    {
        const int rateLimit = 20 * 1024; // 20 KBps

        // Example: Download a file with rate limiting
        DownloadFileWithRateLimit("https://example.com/largefile.zip", "downloaded_file.zip", rateLimit);

        // Example: Upload a file with rate limiting
        UploadFileWithRateLimit("file_to_upload.zip", "https://example.com/upload", rateLimit);
    }

    static void DownloadFileWithRateLimit(string url, string destinationPath, int bytesPerSecond)
    {
        using (var webClient = new WebClient())
        {
            using (var sourceStream = webClient.OpenRead(url))
            using (var rateLimitedStream = new RateLimitedStream(sourceStream, bytesPerSecond))
            using (var destinationStream = File.Create(destinationPath))
            {
                rateLimitedStream.CopyTo(destinationStream);
            }
        }
        Console.WriteLine($"File downloaded to {destinationPath}");
    }

    static void UploadFileWithRateLimit(string filePath, string url, int bytesPerSecond)
    {
        using (var webClient = new WebClient())
        {
            using (var sourceStream = File.OpenRead(filePath))
            using (var rateLimitedStream = new RateLimitedStream(sourceStream, bytesPerSecond))
            {
                webClient.UploadData(url, StreamToByteArray(rateLimitedStream));
            }
        }
        Console.WriteLine($"File uploaded to {url}");
    }

    static byte[] StreamToByteArray(Stream stream)
    {
        using (var memoryStream = new MemoryStream())
        {
            stream.CopyTo(memoryStream);
            return memoryStream.ToArray();
        }
    }
}
