public class NetworkLimiter
{
    private const uint FWPM_SESSION_FLAG_DYNAMIC = 0x00000001;
    private const uint RPC_C_AUTHN_WINNT = 10;
    private const uint RPC_C_AUTHZ_NONE = 0;
    private const uint RPC_C_AUTHN_LEVEL_DEFAULT = 0;
    private const uint RPC_C_IMP_LEVEL_IMPERSONATE = 3;

    [DllImport("fwpuclnt.dll")]
    private static extern uint FwpmEngineOpen0(string serverName, uint authnService, IntPtr authIdentity, IntPtr session, out IntPtr engineHandle);

    [DllImport("fwpuclnt.dll")]
    private static extern uint FwpmEngineClose0(IntPtr engineHandle);

    [DllImport("fwpuclnt.dll")]
    private static extern uint FwpmFilterAdd0(IntPtr engineHandle, ref FWPM_FILTER0 filter, IntPtr sd, out ulong id);

    [StructLayout(LayoutKind.Sequential)]
    private struct FWPM_FILTER0
    {
        public Guid filterKey;
        public FWPM_DISPLAY_DATA0 displayData;
        public uint flags;
        public IntPtr providerKey;
        public FWP_BYTE_BLOB providerData;
        public Guid layerKey;
        public Guid subLayerKey;
        public FWP_VALUE0 weight;
        public uint numFilterConditions;
        public IntPtr filterCondition;
        public FWPM_ACTION0 action;
        public IntPtr reserved;
        public ulong filterID;
        public FWP_VALUE0 effectiveWeight;
    }

    [StructLayout(LayoutKind.Sequential)]
    private struct FWPM_DISPLAY_DATA0
    {
        [MarshalAs(UnmanagedType.LPWStr)]
        public string name;
        [MarshalAs(UnmanagedType.LPWStr)]
        public string description;
    }

    [StructLayout(LayoutKind.Sequential)]
    private struct FWP_BYTE_BLOB
    {
        public uint size;
        public IntPtr data;
    }

    [StructLayout(LayoutKind.Sequential)]
    private struct FWP_VALUE0
    {
        public FWP_DATA_TYPE type;
        public IntPtr value;
    }

    private enum FWP_DATA_TYPE
    {
        FWP_EMPTY,
        FWP_UINT8,
        FWP_UINT16,
        FWP_UINT32,
        FWP_UINT64,
        FWP_INT8,
        FWP_INT16,
        FWP_INT32,
        FWP_INT64,
        FWP_FLOAT,
        FWP_DOUBLE,
        FWP_BYTE_ARRAY16_TYPE,
        FWP_BYTE_BLOB_TYPE,
        FWP_SID,
        FWP_SECURITY_DESCRIPTOR_TYPE,
        FWP_TOKEN_INFORMATION_TYPE,
        FWP_TOKEN_ACCESS_INFORMATION_TYPE,
        FWP_UNICODE_STRING_TYPE
    }

    [StructLayout(LayoutKind.Sequential)]
    private struct FWPM_ACTION0
    {
        public FWP_ACTION_TYPE type;
        public IntPtr calloutKey;
    }

    private enum FWP_ACTION_TYPE
    {
        FWP_ACTION_BLOCK,
        FWP_ACTION_PERMIT,
        FWP_ACTION_CALLOUT_TERMINATING,
        FWP_ACTION_CALLOUT_INSPECTION,
        FWP_ACTION_CALLOUT_UNKNOWN
    }

    private IntPtr engineHandle;

    public void StartLimiting(int kbpsLimit)
    {
        uint result = FwpmEngineOpen0(null, RPC_C_AUTHN_WINNT, IntPtr.Zero, IntPtr.Zero, out engineHandle);
        if (result != 0)
        {
            throw new Exception($"Failed to open WFP engine. Error code: {result}");
        }

        FWPM_FILTER0 filter = new FWPM_FILTER0
        {
            displayData = new FWPM_DISPLAY_DATA0
            {
                name = "Network Limiter Filter",
                description = "Limits network traffic"
            },
            action = new FWPM_ACTION0
            {
                type = FWP_ACTION_TYPE.FWP_ACTION_CALLOUT_INSPECTION
            }
        };

        ulong filterId;
        result = FwpmFilterAdd0(engineHandle, ref filter, IntPtr.Zero, out filterId);
        if (result != 0)
        {
            throw new Exception($"Failed to add WFP filter. Error code: {result}");
        }

        Console.WriteLine($"Network limiter started. Limit: {kbpsLimit} kbps");

        // Start a thread to enforce the rate limit
        Thread limitEnforcerThread = new Thread(() => EnforceRateLimit(kbpsLimit));
        limitEnforcerThread.Start();
    }

    private void EnforceRateLimit(int kbpsLimit)
    {
        long bytesSent = 0;
        long bytesReceived = 0;
        long startTime = DateTime.Now.Ticks;

        while (true)
        {
            long currentTime = DateTime.Now.Ticks;
            long elapsedMilliseconds = (currentTime - startTime) / TimeSpan.TicksPerMillisecond;

            if (elapsedMilliseconds >= 1000)
            {
                long totalBytes = bytesSent + bytesReceived;
                long allowedBytes = kbpsLimit * 1024;

                if (totalBytes > allowedBytes)
                {
                    long excessBytes = totalBytes - allowedBytes;
                    long sleepTime = (excessBytes * 1000) / (kbpsLimit * 1024);
                    Thread.Sleep((int)sleepTime);
                }

                bytesSent = 0;
                bytesReceived = 0;
                startTime = currentTime;
            }

            // Here you would update bytesSent and bytesReceived based on actual network activity
            // This example uses dummy values for illustration
            bytesSent += 1024;
            bytesReceived += 1024;

            Thread.Sleep(10);
        }
    }

    public void StopLimiting()
    {
        if (engineHandle != IntPtr.Zero)
        {
            FwpmEngineClose0(engineHandle);
            engineHandle = IntPtr.Zero;
        }
        Console.WriteLine("Network limiter stopped");
    }
}
