```csharp
using System;
using System.Collections.Generic;
using System.Net;
using System.Threading;
using System.Threading.Tasks;
using PacketDotNet;
using SharpPcap;

public class NetworkLimiter
{
    private readonly Dictionary<IPAddress, long> _bytesPerSecond = new Dictionary<IPAddress, long>();
    private readonly Dictionary<IPAddress, long> _lastUpdateTime = new Dictionary<IPAddress, long>();
    private readonly int _rateLimit;
    private CancellationTokenSource _cts;
    private long _totalPacketsProcessed = 0;

    public NetworkLimiter(int rateLimitKbps)
    {
        _rateLimit = rateLimitKbps * 1024; // Convert to bytes per second
    }

    public void Start()
    {
        _cts = new CancellationTokenSource();
        Task.Run(() => CapturePackets(_cts.Token));
    }

    public void Stop()
    {
        _cts?.Cancel();
    }

    private void CapturePackets(CancellationToken cancellationToken)
    {
        var devices = CaptureDeviceList.Instance;
        if (devices.Count < 1)
        {
            Console.WriteLine("No capture devices found.");
            return;
        }

        // List all devices
        for (int i = 0; i < devices.Count; i++)
        {
            Console.WriteLine($"{i}: {devices[i].Description}");
        }

        // Ask user to choose a device
        Console.Write("Enter the number of the device you want to monitor: ");
        if (!int.TryParse(Console.ReadLine(), out int deviceIndex) || deviceIndex < 0 || deviceIndex >= devices.Count)
        {
            Console.WriteLine("Invalid device number.");
            return;
        }

        ICaptureDevice device = devices[deviceIndex];

        try
        {
            device.Open();
            device.Filter = "ip"; // Capture only IP packets

            Console.WriteLine($"Listening on {device.Description}");
            Console.WriteLine("Packet capture started. Waiting for network activity...");

            device.OnPacketArrival += (sender, e) =>
            {
                var rawPacket = e.GetPacket();
                var packet = Packet.ParsePacket(rawPacket.LinkLayerType, rawPacket.Data);
                var ipPacket = packet.Extract<IPPacket>();

                if (ipPacket != null)
                {
                    ProcessPacket(ipPacket);
                }
            };

            device.StartCapture();

            while (!cancellationToken.IsCancellationRequested)
            {
                Thread.Sleep(5000); // Sleep for 5 seconds
                Console.WriteLine($"Total packets processed: {_totalPacketsProcessed}");
            }
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Error capturing packets: {ex.Message}");
        }
        finally
        {
            device.StopCapture();
            device.Close();
        }
    }

    private void ProcessPacket(IPPacket ipPacket)
    {
        var sourceIp = ipPacket.SourceAddress;
        var destinationIp = ipPacket.DestinationAddress;
        var packetSize = ipPacket.TotalLength;

        UpdateTrafficRate(sourceIp, packetSize);
        UpdateTrafficRate(destinationIp, packetSize);

        Console.WriteLine($"Packet: {sourceIp} -> {destinationIp}, Size: {packetSize} bytes");

        if (ShouldLimitTraffic(sourceIp) || ShouldLimitTraffic(destinationIp))
        {
            Console.WriteLine($"Limiting traffic for {sourceIp} or {destinationIp}");
        }

        Interlocked.Increment(ref _totalPacketsProcessed);
    }

    private void UpdateTrafficRate(IPAddress ip, int packetSize)
    {
        long currentTime = DateTimeOffset.UtcNow.ToUnixTimeMilliseconds();

        if (!_bytesPerSecond.ContainsKey(ip))
        {
            _bytesPerSecond[ip] = 0;
            _lastUpdateTime[ip] = currentTime;
        }

        _bytesPerSecond[ip] += packetSize;

        if (currentTime - _lastUpdateTime[ip] >= 1000)
        {
            _bytesPerSecond[ip] = packetSize;
            _lastUpdateTime[ip] = currentTime;
        }
    }

    private bool ShouldLimitTraffic(IPAddress ip)
    {
        return _bytesPerSecond.ContainsKey(ip) && _bytesPerSecond[ip] > _rateLimit;
    }
}
```

