int main()
{
    HANDLE handle;
    WINDIVERT_ADDRESS addr;
    char packet[WINDIVERT_MTU_MAX];
    UINT packet_len;
    UINT64 total_bytes = 0;

    // Open the WinDivert handle
    handle = WinDivertOpen("true", WINDIVERT_LAYER_NETWORK, 0, 0);
    if (handle == INVALID_HANDLE_VALUE)
    {
        std::cerr << "Error opening WinDivert handle" << std::endl;
        return 1;
    }

    std::cout << "Starting packet capture..." << std::endl;

    while (true)
    {
        // Read a packet
        if (!WinDivertRecv(handle, packet, sizeof(packet), &packet_len, &addr))
        {
            std::cerr << "Failed to read packet" << std::endl;
            continue;
        }

        // Process the packet (in this example, just count bytes)
        total_bytes += packet_len;
        std::cout << "Received packet: " << packet_len << " bytes. Total: " << total_bytes << " bytes" << std::endl;

        // Reinject the packet
        if (!WinDivertSend(handle, packet, packet_len, NULL, &addr))
        {
            std::cerr << "Failed to reinject packet" << std::endl;
        }
    }

    WinDivertClose(handle);
    return 0;
}
