#include "WFPEngine.h"
#include <windows.h>
#include <fwpmu.h>
#include <fwpmtypes.h>

HANDLE engineHandle = NULL;

WFPENGINE_API bool Initialize() {
    if (engineHandle) return true;

    FWPM_SESSION0 session = {0};
    session.flags = FWPM_SESSION_FLAG_DYNAMIC;

    DWORD result = FwpmEngineOpen0(NULL, RPC_C_AUTHN_WINNT, NULL, &session, &engineHandle);
    return (result == ERROR_SUCCESS);
}

WFPENGINE_API bool AddMonitoringFilter() {
    if (!engineHandle) return false;

    FWPM_FILTER0 filter = {0};
    FWPM_FILTER_CONDITION0 condition[1] = {0};
    UINT64 filterId = 0;

    filter.layerKey = FWPM_LAYER_INBOUND_TRANSPORT_V4;
    filter.action.type = FWP_ACTION_PERMIT;
    filter.weight.type = FWP_EMPTY;
    filter.numFilterConditions = 1;
    filter.filterCondition = condition;

    condition[0].fieldKey = FWPM_CONDITION_IP_PROTOCOL;
    condition[0].matchType = FWP_MATCH_EQUAL;
    condition[0].conditionValue.type = FWP_UINT8;
    condition[0].conditionValue.uint8 = IPPROTO_TCP;

    DWORD result = FwpmFilterAdd0(engineHandle, &filter, NULL, &filterId);
    return (result == ERROR_SUCCESS);
}

WFPENGINE_API bool RemoveFilter(unsigned long long filterId) {
    if (!engineHandle) return false;

    DWORD result = FwpmFilterDeleteById0(engineHandle, filterId);
    return (result == ERROR_SUCCESS);
}

WFPENGINE_API void Cleanup() {
    if (engineHandle) {
        FwpmEngineClose0(engineHandle);
        engineHandle = NULL;
    }
}
