bool __stdcall AddBandwidthFilter(const wchar_t* appPath, UINT32 uploadLimit, UINT32 downloadLimit) {
    if (!engineHandle) {
        std::wcout << L"Engine handle is NULL" << std::endl;
        return false;
    }

    // Remove existing filter for this app if it exists
    RemoveBandwidthFilter(appPath);

    FWPM_FILTER0 filter = {0};
    FWPM_FILTER_CONDITION0 conditions[1] = {0};
    UINT64 filterId = 0;

    filter.providerKey = (GUID*)&PROVIDER_KEY;
    filter.layerKey = FWPM_LAYER_ALE_FLOW_ESTABLISHED_V4;
    filter.subLayerKey = FWPM_SUBLAYER_UNIVERSAL;
    filter.weight.type = FWP_EMPTY;
    filter.action.type = FWP_ACTION_CALLOUT_TERMINATING;
    filter.action.calloutKey = FWPM_CALLOUT_IPSEC_INBOUND_TRANSPORT_V4;  // This is a placeholder, you'll need to implement a custom callout
    filter.flags = FWPM_FILTER_FLAG_NONE;
