    conditions[0].fieldKey = FWPM_CONDITION_ALE_APP_ID;
    conditions[0].matchType = FWP_MATCH_EQUAL;
    conditions[0].conditionValue.type = FWP_BYTE_BLOB_TYPE;
    conditions[0].conditionValue.byteBlob = NULL;  // You'll need to implement this to get the app ID from the path

    filter.displayData.name = const_cast<wchar_t*>(L"Bandwidth Limit Filter");
    filter.displayData.description = const_cast<wchar_t*>(L"Limits bandwidth for specific application");

    DWORD result = FwpmFilterAdd0(engineHandle, &filter, NULL, &filterId);
    if (result != ERROR_SUCCESS) {
        std::wcout << L"FwpmFilterAdd0 failed with error: " << result << std::endl;
        return false;
    }

    filterIds[appPath] = filterId;
    std::wcout << L"Filter added successfully for " << appPath << L". Filter ID: " << filterId << std::endl;
    return true;
}

bool __stdcall RemoveBandwidthFilter(const wchar_t* appPath) {
    if (!engineHandle) {
        std::wcout << L"Engine handle is NULL" << std::endl;
        return false;
    }

    auto it = filterIds.find(appPath);
    if (it == filterIds.end()) {
        std::wcout << L"No filter found for " << appPath << std::endl;
        return true;
    }

    DWORD result = FwpmFilterDeleteById0(engineHandle, it->second);
    if (result != ERROR_SUCCESS) {
        std::wcout << L"FwpmFilterDeleteById0 failed with error: " << result << std::endl;
        return false;
    }

    filterIds.erase(it);
    std::wcout << L"Filter removed successfully for " << appPath << std::endl;
    return true;
}

void __stdcall Cleanup() {
    if (engineHandle) {
        for (const auto& pair : filterIds) {
            FwpmFilterDeleteById0(engineHandle, pair.second);
        }
        filterIds.clear();

        FwpmProviderDeleteByKey0(engineHandle, &PROVIDER_KEY);
        DWORD result = FwpmEngineClose0(engineHandle);
        if (result != ERROR_SUCCESS) {
            std::wcout << L"FwpmEngineClose0 failed with error: " << result << std::endl;
        }
        engineHandle = NULL;
    }
}

