        private static extern bool AddBandwidthFilter(string appPath, uint uploadLimit, uint downloadLimit);

        [DllImport("newWFPEngine.dll")]
        private static extern bool RemoveBandwidthFilter(string appPath);

        [DllImport("newWFPEngine.dll")]
        private static extern void Cleanup();

        private Dictionary<string, (long lastUpload, long lastDownload)> lastNetworkUsage = new Dictionary<string, (long, long)>();

        public Form1()
        {
            InitializeComponent();
            if (!Initialize())
            {
                MessageBox.Show("Failed to initialize WFP Engine", "Error", MessageBoxButtons.OK, MessageBoxIcon.Error);
            }
            PopulateApplicationList();
            updateTimer.Start();
        }

        private void PopulateApplicationList()
        {
            lvApplications.Items.Clear();
            foreach (Process process in Process.GetProcesses())
            {
                try
                {
                    if (!string.IsNullOrEmpty(process.MainModule.FileName))
                    {
                        ListViewItem item = new ListViewItem(process.MainModule.FileName);
                        item.SubItems.Add("0"); // Upload Limit
                        item.SubItems.Add("0"); // Download Limit
                        item.SubItems.Add("0"); // Upload Usage
                        item.SubItems.Add("0"); // Download Usage
                        item.Tag = process.Id;
                        lvApplications.Items.Add(item);
                    }
                }
                catch { } // Ignore processes we can't access
            }
        }

        private void updateTimer_Tick(object sender, EventArgs e)
        {
            foreach (ListViewItem item in lvApplications.Items)
            {
                int processId = (int)item.Tag;
                try
                {
                    Process process = Process.GetProcessById(processId);
                    string appPath = process.MainModule.FileName;

                    if (!lastNetworkUsage.ContainsKey(appPath))
                    {
                        lastNetworkUsage[appPath] = (0, 0);
                    }

                    long uploadBytes = 0;
                    long downloadBytes = 0;

                    foreach (NetworkInterface ni in NetworkInterface.GetAllNetworkInterfaces())
                    {
                        uploadBytes += ni.GetIPv4Statistics().BytesSent;
                        downloadBytes += ni.GetIPv4Statistics().BytesReceived;
                    }

                    long uploadDiff = uploadBytes - lastNetworkUsage[appPath].lastUpload;
                    long downloadDiff = downloadBytes - lastNetworkUsage[appPath].lastDownload;

                    lastNetworkUsage[appPath] = (uploadBytes, downloadBytes);

                    item.SubItems[3].Text = $"{uploadDiff / 1024} KB/s";
                    item.SubItems[4].Text = $"{downloadDiff / 1024} KB/s";
                }
                catch
                {
                    // Process may have exited
                    item.SubItems[3].Text = "N/A";
                    item.SubItems[4].Text = "N/A";
