        private static extern void Cleanup();

        private Dictionary<int, (long upload, long download)> networkUsage = new Dictionary<int, (long, long)>();
        private PerformanceCounterCategory performanceCounterCategory;
        private Thread updateThread;
        private volatile bool isRunning = true;

        public Form1()
        {
            InitializeComponent();
            if (!Initialize())
            {
                MessageBox.Show("Failed to initialize WFP Engine", "Error", MessageBoxButtons.OK, MessageBoxIcon.Error);
            }
            performanceCounterCategory = new PerformanceCounterCategory("Process");
            PopulateApplicationList();
            StartUpdateThread();
        }

        private void PopulateApplicationList()
        {
            lvApplications.Items.Clear();
            foreach (Process process in Process.GetProcesses())
            {
                try
                {
                    if (!string.IsNullOrEmpty(process.MainModule?.FileName))
                    {
                        ListViewItem item = new ListViewItem(process.MainModule.FileName);
                        item.SubItems.Add("0"); // Upload Limit
                        item.SubItems.Add("0"); // Download Limit
                        item.SubItems.Add("0"); // Upload Usage
                        item.SubItems.Add("0"); // Download Usage
                        item.Tag = process.Id;
                        lvApplications.Items.Add(item);
                    }
                }
                catch { } // Ignore processes we can't access
            }
        }

        private void StartUpdateThread()
        {
            updateThread = new Thread(UpdateNetworkUsage);
            updateThread.IsBackground = true;
            updateThread.Start();
        }

        private void UpdateNetworkUsage()
        {
            while (isRunning)
            {
                var instanceNames = performanceCounterCategory.GetInstanceNames();
                var sendCounters = new PerformanceCounter("Process", "IO Write Bytes/sec", instanceNames, true);
                var receiveCounters = new PerformanceCounter("Process", "IO Read Bytes/sec", instanceNames, true);

                foreach (string instanceName in instanceNames)
                {
                    try
                    {
                        int processId = Process.GetProcessesByName(instanceName).FirstOrDefault()?.Id ?? -1;
                        if (processId != -1)
                        {
                            long send = (long)sendCounters.GetCounterValue(instanceName);
                            long receive = (long)receiveCounters.GetCounterValue(instanceName);
                            networkUsage[processId] = (send, receive);
                        }
                    }
                    catch { } // Ignore any errors
                }

                sendCounters.Dispose();
                receiveCounters.Dispose();

                Thread.Sleep(1000); // Update every second
            }
        }

        private void updateTimer_Tick(object sender, EventArgs e)
        {
            if (lvApplications.Items.Count == 0) return;

            int firstVisibleIndex = lvApplications.TopItem.Index;
            int lastVisibleIndex = firstVisibleIndex + lvApplications.ClientSize.Height / lvApplications.Items[0].Bounds.Height;
            lastVisibleIndex = Math.Min(lastVisibleIndex, lvApplications.Items.Count - 1);

            for (int i = firstVisibleIndex; i <= lastVisibleIndex; i++)
            {
                ListViewItem item = lvApplications.Items[i];
                int processId = (int)item.Tag;
                if (networkUsage.TryGetValue(processId, out var usage))
                {
                    item.SubItems[3].Text = $"{usage.upload / 1024} KB/s";
                    item.SubItems[4].Text = $"{usage.download / 1024} KB/s";
                }
                else
                {
                    item.SubItems[3].Text = "N/A";
                    item.SubItems[4].Text = "N/A";
                }
            }
        }

        // ... (rest of the methods remain the same)

        protected override void OnFormClosing(FormClosingEventArgs e)
        {
            base.OnFormClosing(e);
            isRunning = false;
            updateThread.Join(1000); // Wait for the update thread to finish
            Cleanup();
        }
    }
}
